import React, { Component } from 'react';
import NavBar from './components/NavBar.jsx';

class App extends Component {

  state = {
    isMouseDown: false,
    disabled: false
  }

  handleMouseDown = (e) => {
    if(!this.state.disabled)
      this.setState({ isMouseDown: true });
    e.preventDefault();
  }

  handleMouseUp = (e) => {
    if(!this.state.disabled)
      this.setState({ isMouseDown: false });
    e.preventDefault();
  }

  handleDisableReadMouse = (val) => {
      this.setState({
        disabled: !val,
        isMouseDown: false
      });
  }

  render() {
    // console.log("App", this.state.disabled)
    return (
      <div style={{ backgroundColor: blue}} onMouseDown={this.handleMouseDown} onMouseUp={this.handleMouseUp}>
        <NavBar />
      </div>
     );
  }
}

export default App;
