import React, { Component } from 'react';
import NavBar from './components/NavBar.jsx';
// import Main from './components/Main.jsx';

class App extends Component {

  state = {
    isMouseDown: false,
    disabled: false,
    time: new Date().getTime()
  }

  handleMouseDown = (e) => {
    if(!this.state.disabled)
      this.setState({ isMouseDown: true });
    e.preventDefault();
  }

  handleMouseUp = (e) => {
    if(!this.state.disabled)
      this.setState({ isMouseDown: false });
    e.preventDefault();
  }

  handleDisableReadMouse = (val) => {
      this.setState({
        disabled: !val,
        isMouseDown: false
      });
  }

  render() {
    // console.log("App", this.state.disabled)
    var d = new Date();
    var n = d.getTime();
    let diff = n - this.state.time;
    return (
      <div className="gamespace" onMouseDown={this.handleMouseDown} onMouseUp={this.handleMouseUp}>
        <NavBar />
        <div style={{color:'white'}}>{diff}</div>
      </div>
     );
  }
}

export default App;
